import React, { useState, useEffect } from 'react';

import Card from '../../shared/components/UIElements/Card';
import './GetAllQuestions.css';
import Button from "../../shared/components/Button/Button";
import Modal from "../../Model/Model";
import { useForm } from '../../shared/Hooks/form-hook';
import ErrorModel from '../../Model/ErorrModel';
import axios from "axios";
import QuestionItem from '../components/QuestionItem';
import QuestionList from '../components/QuestionList';
const CreateExamDef = props => {

    const [loadedQuestions, setLoadedQuestions] = useState();
    const [isLoading, setIsLoading] = useState(true);
    // const questionId = useParams().questionId;
    const [error, setError] = useState();

    const [checkedState, setCheckedState] = useState([]);
    const [SavedQuestions, setSavedQuestions] = useState([{}]);


    const [formState, inputHandler, setFormData] = useForm(
        {
            name: {
                value: "",
                isValid: false
            },
            category: {
                value: "",
                isValid: false
            },
            subCategory: {
                value: "",
                isValid: false
            },
            mark: {
                value: "",
                isValid: false
            },
            expextedTime: {
                value: "",
                isValid: false
            }
            ,
            correctAnswers: {
                value: "",
                isValid: false
            }
            ,
            createdAt: {
                value: "",
                isValid: false
            },
            answers: {
                answer: {
                    value: "",
                    isValid: false
                },
                description: {
                    value: "",
                    isValid: false
                }
            }
        },
        false
    );



    useEffect(() => {
        // console.log("test exam get all questions");
        const fetchQuestion = async () => {
            setIsLoading(true);
            try {
                console.log("test try in get all questions")
                const response = await axios.get(
                    "http://localhost:5000/api/questions"
                );

                const responseData = await response.data;

                if (response.status !== 200) {
                    throw new Error(responseData.message);
                }

                setLoadedQuestions(responseData.questions);

                setCheckedState(new Array(loadedQuestions.length).fill(false));


                setFormData(
                    {
                        name: {
                            value: responseData.questions.name,
                            isValid: true
                        },
                        category: {
                            value: responseData.questions.category,
                            isValid: true
                        },
                        subCategory: {
                            value: responseData.questions.subCategory,
                            isValid: true
                        },
                        mark: {
                            value: responseData.questions.mark,
                            isValid: true
                        },
                        expextedTime: {
                            value: responseData.questions.expextedTime,
                            isValid: true
                        }
                        ,
                        correctAnswers: {
                            value: responseData.questions.correctAnswers,
                            isValid: true
                        }
                        ,
                        createdAt: {
                            value: responseData.questions.createdAt,
                            isValid: true
                        },
                        // answers: [{
                        //   // name: {
                        //   //   value: "",
                        //   //   isValid: true
                        //   // },
                        //   answer: {
                        //     value: responseData.data.questions.answers[0].answer,
                        //     isValid: true
                        //   },
                        //   description: {
                        //     value: responseData.data.questions.answers[0].description,
                        //     isValid: true
                        //   }
                        // }

                    },
                    true
                );




            } catch (err) {
                console.log(err);
                setError(err.message);
            }
            setIsLoading(false);
        };
        fetchQuestion();
    }, [setFormData]);



    if (isLoading) {
        return (
            <div className="center">
                <h2>Loading...</h2>
            </div>
        );
    }
    if (!loadedQuestions && !error) {
        return (<div className="center">
            <Card>
                <h2>Could not find place!</h2>
            </Card>
        </div>);
    }
    const errorHandler = () => {
        setError(null);
    }

    const placeSubmitHandler = (e) => {
        e.preventDefault();
    }

    // let newArr=new Array(loadedQuestions.length);
    // setCheckedState(newArr)


    const handleOnChange = (position) => {
        const updatedCheckedState = checkedState.map((item, index) =>
            index === position ? !item : item
        );
        setCheckedState(updatedCheckedState);
        // checkedState.map((x) => {
        //     console.log(x);
        // });'
        handle();
    }
    const handle = (position) => {
        // const updatedCheckedState = checkedState.map((item, index) =>
        //     index === position ? !item : item
        // );
        // setCheckedState(updatedCheckedState);
        checkedState.map((x) => {
            // console.log(checkedState);
        });
    }

    return (
        <React.Fragment>
            <ErrorModel error={error} onClear={errorHandler} />
            <form className="place-form" onSubmit={placeSubmitHandler}>
                <div class="input-group mb-3">
                    <span class="input-group-text" id="inputGroup-sizing-default">Exam Name</span>
                    <input type="text" class="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />

                </div>

                <div class="input-group mb-3">
                    <span class="input-group-text" id="inputGroup-sizing-default">Passing Score</span>
                    <input type="text" class="form-control" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-default" />
                </div>
                <span class="input-group-text" id="inputGroup-sizing-default">choose questions</span>
                {loadedQuestions.map(({ name }, index) => {
                    return (
                        // <li key={index}>
                        <div className="toppings-list-item">
                            <div className="left-section">
                                <input
                                    type="checkbox"
                                    id={loadedQuestions[index]._id}
                                    name={loadedQuestions[index].name}
                                    value={loadedQuestions[index].name}
                                    checked={setCheckedState[index]}
                                    onChange={() => handleOnChange(index)}
                                />

                                <label >{name}</label>
                            </div>

                            {/* <div className="right-section">{getFormattedPrice(price)}</div> */}
                        </div>
                    );

                })}
                {/* {console.log(checkedState)} */}
            </form>

        </React.Fragment>
    );
};

export default CreateExamDef;
