import React, { useState,useEffect } from 'react';

import Card from '../../shared/components/UIElements/Card';
import './GetAllQuestions.css';
import Button from "../../shared/components/Button/Button";
import Modal from "../../Model/Model";
import { useForm } from '../../shared/Hooks/form-hook';
import ErrorModel from '../../Model/ErorrModel';
import axios from "axios";
const GetAllQuestions = props => {

  const [loadedQuestions, setLoadedQuestions] = useState();
  const [isLoading, setIsLoading] = useState(true);
  // const questionId = useParams().questionId;
  const [error, setError] = useState();
  // console.log(questionId);
  // const auth = useContext(AuthContext);


  const [formState, inputHandler, setFormData] = useForm(
    {
      name: {
        value: "",
        isValid: false
      }
    }
  );


  useEffect(() => {
    console.log("test exam get all questions");
    const fetchQuestion = async () => {
      setIsLoading(true);
      try {
        console.log("test try in get all questions")
        const responseData = await axios.get(
          "http://localhost:5000/api/questions"
        );
        // console.log(responseData.data.questions)
        const resDataQuestions = await responseData.data.questions;
        // const u=`http://localhost:5000/api/questions/user/${questionId}`;
        // console.log(u);
        // console.log(questionId);
        // console.log(responseData.data.question);
        setLoadedQuestions(responseData.data.questions);
        // console.log("loaded questions")
        // console.log(loadedQuestions);
        // console.log(responseData.data.question.answers[0].description);
        setFormData(
          {
            name: {
              value: loadedQuestions.name,
              isValid: true
            }
            
          },
          true
        );


        console.log(formState);

      } catch (err) {
        console.log("zft erorr");
        setError(err.message);
      }
      setIsLoading(false);
    };
    fetchQuestion();
  }, [setFormData]);

  if (isLoading) {
    return (
      <div className="center">
        <h2>Loading...</h2>
      </div>
    );
  }
  if (!loadedQuestions && !error) {
    return (<div className="center">
      <Card>
        <h2>Could not find place!</h2>
      </Card>
    </div>);
  }

  const errorHandler = () => {
    setError(null);
  }

  return (
    <React.Fragment>
      <ErrorModel error={error} onClear={errorHandler} />
      {!isLoading && loadedQuestions && (


        <div className="topping">

          <input
            type="checkbox"
            id="topping"
            name="topping"
            value="Paneer"
            // checked={isChecked}
            // onChange={handleOnChange}
          />
        </div>
      )}
      
   
    </React.Fragment>
  );
};

export default GetAllQuestions;
